var SimpleTabs = new Class({

	Implements: [Events, Options],

	/**
	 * Options
	 */
	options: {
		show: 0,
		selector: '.tab-tab',
		classWrapper: 'tab-wrapper',
		classMenu: 'tab-menu',
		classMenuWrapper: 'tab-menu-wrapper',
		classMenuScroller: 'tab-menu-scroller',
		classContainer: 'tab-container',
		onSelect: function(toggle, container, index) {
			toggle.addClass('tab-selected');
			container.setStyle('display', '');
		},
		onDeselect: function(toggle, container, index) {
			toggle.removeClass('tab-selected');
			container.setStyle('display', 'none');
		},
		onRequest: function(toggle, container, index) {
			container.addClass('tab-ajax-loading');
		},
		onComplete: function(toggle, container, index) {
			container.removeClass('tab-ajax-loading');
		},
		onFailure: function(toggle, container, index) {
			container.removeClass('tab-ajax-loading');
		},
		onAdded: Class.empty,
		getContent: null,
		ajaxOptions: {},
		cache: true
	},

	/**
	 * Constructor
	 *
	 * @param	{Element} The parent Element that holds the tab elements
	 * @param	{Object} Options
	 */
	initialize: function(element, options) {
		this.element = $(element);
		this.setOptions(options);
		this.selected = null;
		this.build();
	},

	build: function() {
		this.tabs = [];
		this.menuWrapper = new Element('div').addClass(this.options.classMenuWrapper);
		this.menuScroller = new Element('div').addClass(this.options.classMenuScroller).injectTop(this.menuWrapper);
		this.menu = new Element('ul', {'class': this.options.classMenu}).injectTop(this.menuScroller);
		this.wrapper = new Element('div', {'class': this.options.classWrapper});

		this.element.getElements(this.options.selector).each(function(el) {
			var content = el.get('href') || (this.options.getContent ? this.options.getContent.call(this, el) : el.getNext());
			this.addTab(el.innerHTML, el.title || el.innerHTML, content);
		}, this);
		this.element.empty().adopt(this.menuWrapper, this.wrapper);
		var select = document.location.href.split('#')[1];
		if(select) select -= 1;
		var length = 0;
		var max_tab_size = 0;
		this.tabs.each(function(tab){
			var size = tab.toggle.getSize().x;
			if(size > max_tab_size) max_tab_size = size;
			length += size;
		});
		
		
		if(length > this.menuWrapper.getSize().x){
			this.menuScroller.setStyle('width',length+max_tab_size);
			this.scroller = new Fx.Scroll(this.menuWrapper);
			var step = 250;
			this.menuWrapper.addEvent('mouseover', function(e){
				var e = new Event(e);
				if((this.menuWrapper.getSize().x - e.client.x) < step){
				 this.scroller.start(this.menuWrapper.getScroll().x+step);
				}
				else if(e.client.x <  step && e.client.x > 0){
				 this.scroller.start(this.menuWrapper.getScroll().x-step);
				}
			}.bind(this));
		}
		if (this.tabs.length) this.select(select || this.options.show);
		window.addEvent('keydown',function(e){
			var e = new Event(e);
				if(e.control && e.key == 'left'){
					if(this.selected == 0){
						var next = (this.tabs.length -1);
					}
					else var next = this.selected-1;
					this.select(next);				
					if(this.scroller) this.scroller.toElement(this.tabs[next].toggle);
				}
				else if(e.control && e.key == 'right'){
					if(this.selected == (this.tabs.length -1)){
						var next = 0;
					}
					else var next = this.selected+1;
					this.select(next);
					if(this.scroller) this.scroller.toElement(this.tabs[next].toggle);
				}
		}.bind(this));
	},

	/**
	 * Add a new tab at the end of the tab menu
	 *
	 * @param	{String} inner Text
	 * @param	{String} Title
	 * @param	{Element|String} Content Element or URL for Ajax
	 */
	addTab: function(text, title, content) {
		var grab = $(content);
		var container = (grab || new Element('div'))
			.setStyle('display', 'none')
			.addClass(this.options.classContainer)
			.inject(this.wrapper);
		var pos = this.tabs.length;
		var evt = (this.options.hover) ? 'mouseenter' : 'click';
		var tab = {
			container: container,
			toggle: new Element('li').grab(new Element('a', {
				href: 'javascript:void(0)',
				id: 'tab-handle-'+(this.tabs.length+1),
				title: title
			}).grab(
				new Element('span', {html: text})
			)).addEvent(evt, this.onClick.bindWithEvent(this, [pos])).inject(this.menu)
			//.addEvent('click',function(){ new Fx.Scroll(window).set(null,(this.menuWrapper.getPosition().y))}.bind(this))
		};
		if (!grab && $type(content) == 'string') tab.url = content;
		this.tabs.push(tab);
		return this.fireEvent('onAdded', [tab.toggle, tab.container, pos]);
	},

	onClick: function(evt, index) {
		this.select(index);
		document.location.href = document.location.href.split('#')[0]+'#'+(index+1);
		return false;
	},

	/**
	 * Select the tab via tab-index
	 *
	 * @param	{Number} Tab-index
	 */
	select: function(index) {
		if (this.selected === index || !this.tabs[index]) return this;
		if (this.ajax) this.ajax.cancel().removeEvents();
		var tab = this.tabs[index];
		var params = [tab.toggle, tab.container, index];
		if (this.selected !== null) {
			var current = this.tabs[this.selected];
			if (this.ajax && this.ajax.running) this.ajax.cancel();
			params.extend([current.toggle, current.container, this.selected]);
			this.fireEvent('onDeselect', [current.toggle, current.container, this.selected]);
		}
		this.fireEvent('onSelect', params);
		if (tab.url && (!tab.loaded || !this.options.cache)) {
			this.ajax = this.ajax || new Request.HTML();
			this.ajax.setOptions({
				url: tab.url,
				method: 'get',
				update: tab.container,
				onFailure: this.fireEvent.pass(['onFailure', params], this),
				onComplete: function(resp) {
					tab.loaded = true;
					this.fireEvent('onComplete', params);
				}.bind(this)
			}).setOptions(this.options.ajaxOptions);
			this.ajax.send();
			this.fireEvent('onRequest', params);
		}
		this.selected = index;
		return this;
	}

});
function create_preview(form){
    var form = form ? $(form) : $('createdit');
    var textarea = form.getElement('textarea[name=body]');
    if($('preview')) var value =  $('preview').get('value');
    form.addEvent('submit',function(){
	if(form.getElement('[name=action]').value == 'preview'){
		if(!$('preview-layer')){
			new Request.JSON({
			url : '/ajax/parser/preview/',
			data : form.toQueryString(),
			onRequest : function(){
				loader.inline(form.preview.getParent(),'after');	
			    $('preview').set('disabled',true);
			},
			onComplete : function(response){
				$('preview').removeProperty('disabled');
				if(!$chk(response)) return;
				if(response.success){
						  var preview = new Element('div').set('id','preview-layer').addClass('preview').setStyle('min-height',textarea.getStyle('height')).setStyle('width',textarea.getStyle('width')).inject(textarea,'before');
						  var node = new Element('div').addClass('node').inject(preview,'top');
						  var body = new Element('div').addClass('body').inject(node,'top').set('html',response.msg);
						  textarea.setStyle('display','none');
						  preview.addEvent('click',function(){
							  $('preview-layer').destroy();
							  textarea.setStyle('display','block');
							  $('preview').set('value',value);
						  });
						  $('preview').set('value',lang.edit.edit);
						  var p = $('preview-layer').getPosition();
						  new Fx.Scroll(window,{ duration: 500,wait: false}).start(0,p.y-window.getSize().y/2);
				}
				else {
					msg(response.msg);
				}
				loader.inline(form.preview.getParent(),'after',true);
			}
			}).post();
		}	
		else {
			new Fx.Scroll(window,{ duration: 1000}).toElement(form);
			textarea.setStyle('display','block');
			$('preview').set('value',value);
			$('preview-layer').destroy();
		}
		return false;
	}
	if($('preview-layer')){
		$('preview-layer').getNext().setStyle('display','block');
		$('preview').set('value',value);
		$('preview-layer').destroy();
	}
	});
}

window.addEvent('domready',function(){
	$$('form').each(function(form){
		if(form.getElement('#preview')){
			create_preview(form);
		}
	 });
})
