		var differs = ruleArgs[0];
		if(el.value == this.form[differs].value){
			if (this.options.display.titlesInsteadNames)
				var msg = this.options.alerts.differs.replace("%0",this.form[differs].getProperty('title'));
			else if(this.options.display.labelsInsteadNames){
				var msg = this.options.alerts.confirm.replace("%0",this.form[differs].getParent().getElement('label').get('text'));
			}	
			else
				var msg = this.options.alerts.differs.replace("%0",differs);
			el.errors.push(msg);
			return false;
		}
